    var isAPP = Jumei.getCookie("appid")||(/jumei/).test(navigator.userAgent.toLowerCase());
    if(isAPP){
        var get_app_referrer = function () {
            var m = window.location.search.match(/(\?|&)app_referrer\=([^&]*)/);
            if (m) {
                return {'$referrer':decodeURIComponent(decodeURIComponent(m[2]))};
            }
            return {};
        };
        var ua = navigator.userAgent,
            version = ua.match(/jumei\/([\d\.]+)/i)[1],
            isAndroid = (/android/i).test(ua);
        if((isAndroid&&Number(version)>=3.941)||(!isAndroid&&Number(version)>=3.940)){
            if(!isAndroid){
                var iframe = document.createElement("IFRAME");
                iframe.setAttribute("src", "sensorsanalytics://getAppInfo");
                document.documentElement.appendChild(iframe);
                iframe.parentNode.removeChild(iframe);
                iframe = null;
            }
            var cookie = {
                network:Jumei.getCookie('network'),
                idfa:Jumei.getCookie('idfa'),
                imei:Jumei.getCookie('imei'),
                utdid:Jumei.getCookie(isAndroid ? 'unique_device_id' : 'utdid'),
                idfv:Jumei.getCookie('idfv')
            };
            var hasCalled = false;
            setTimeout(function() {
                if (!hasCalled) {
                    hasCalled = true;
                    sa.quick('autoTrack', $.extend(get_app_referrer(), {'$app_version':String(version)},cookie));
                }
            }, 3000);
            sa.getAppStatus(function(app_info){
                if (!hasCalled) {
                    hasCalled = true;
                    sa.identify(app_info.distinct_id);
                    sa.quick('autoTrack', $.extend(get_app_referrer(), {'$app_version':String(version)},cookie));
                }
            });
        }
    }else{
        sa.quick('autoTrack');
    }
</script>



